document.addEventListener("DOMContentLoaded", () => {

    if(document.getElementById('homeSlider')) {
        new Swiper('#homeSlider', {
            spaceBetween: 30,
            navigation: {
                nextEl: '.home-slider .swiper-next',
                prevEl: '.home-slider .swiper-prev',
            },
            pagination: {
                el: '.swiper-pagination',
                clickable: true,
            },
        });
    }

});